/* Hacer un programa que rellene una  matriz de 5x5 connumeros aleatorios y mostrar la matriz resultante*/#include<iostream>#include<stdlib.h>using namespace std;int main() {	int matriz[5][5], dato = 0;	srand(time(NULL));	//Rellenamos la primer matriz	for (int i = 0; i < 5; i++) {		for (int j = 0; j < 5; j++) {			dato = 1 + rand() % (100);			matriz[i][j] = dato;		}	}	for (int i = 0; i < 5; i++) {		for (int j = 0; j < 5; j++) {			cout << matriz[i][j] << "   ";		}		cout << "\n";	}	system("pause");	return 0;}