/* Hacer un programa que rellene dos la matriz de 3x3 y sume ambasmatrices y muestre la sumatoria de la matriz resultante*/#include<iostream>#include<stdlib.h>using namespace std;int main() {	int matriz[3][3], matriz2[3][3], matriz3[3][3];	//Rellenamos la primer matriz	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			cout << "Digite un numero <<[" << i << "][" << j << "]:";			cin >> matriz[i][j];		}		cout << "\n";	}	//Rellenamos la segunda matriz	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			cout << "Digite un numero <<[" << i << "][" << j << "]:";			cin >> matriz2[i][j];		}		cout << "\n";	}	//sumar las matrices	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			matriz3[i][j] = matriz[i][j] + matriz2[i][j];		}		cout << "\n";	}	cout << " Mostrando la primer matriz\n";	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			cout << matriz[i][j] << "  ";		}		cout << "\n";	}	cout << " Mostrando la segunda matriz\n";	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			cout << matriz2[i][j] << "  ";		}		cout << "\n";	}	cout << " Mostrando la sumatoria matriz\n";	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			cout << matriz3[i][j] << "  ";		}		cout << "\n";	}	system("pause");	return 0;}